                this.s.trackDownloadLinks = false;
                this.s.trackExternalLinks = false;
                this.s.trackInlineStats = false;
                this.s.linkDownloadFileTypes = "exe,zip,wav,mp3,mov,mpg,avi,wmv,pdf,doc,docx,xls,xlsx,ppt,pptx";
