                        var hashedUsername = CryptoJS.SHA256(username);
                        this.s.visitorID = hashedUsername;
                        this.s.contextData["user.hashedCID"] = hashedUsername;
                        this.s.contextData["action.login"] = 1;
                        var resultCode = this.s.t();
