            if (item === "ENTER") {
                item = "OK";
            }
            if (item === "FAST_FWD") {
                item = "FF";
            }
            if (item === "REWIND") {
                item = "RW";
            }
            keys.VKey[code] = VKey[item];
            keys.keyCode[item] = code;
