        if (dataCount === -2) {
            return true;
        }

        if (toDsIndex < dataCount) { // witnin range
            return true;
        }

        toDsRow = Math.floor(toDsIndex / curState.cols);
        lastDsRow = Math.floor((dataCount - 1) / curState.cols);

