                ac.UnregisterKey(global.tvKey.KEY_MUTE);
                ac.UnregisterKey(global.tvKey.KEY_POWER);
                ac.UnregisterKey(global.tvKey.KEY_SOURCE);
                ac.UnregisterKey(global.tvKey.KEY_MENU);
